private val PASSWORD_REGEX = "(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%^&+=])(?=\\S+$).{8,}".toRegex()

data class CreateUserResponse(val status: String, val reason: String, val userId: String?)
object UserRouting {
    fun Route.register() {
        post<Locations.Register> {
            val params = call.receive<Parameters>()
            if (!params["password"]!!.matches(PASSWORD_REGEX)) {
                val reason = "Minimum eight characters, at least one letter, one number and one special character"
                call.respond(CreateUserResponse("failed", reason, null))
            } else if (!params["email"]!!.matches(EMAIL_REGEX)) {
                val reason = "Invalid email address"
                call.respond(CreateUserResponse("failed", reason, null))
            } else if (!UserStore.isNewUser(params)) {
                val reason = "This email is already registered"
                call.respond(CreateUserResponse("failed", reason, null))
            } else {
                val userId: String? = UserStore.registerUser(params)?.map { it[User.id] }?.get(0)
                if (userId != null) {
                    call.respond(CreateUserResponse("success", "", userId))
                }
            }
        }
    }
}